// const inputProfiles = $items("Iterate Rows");
const mergeOutput = $input.all().map(item => item.json);

const inputProfiles = mergeOutput.slice(1);
const salesforceProfiles = mergeOutput.slice(0, 1);

// Prepare normalized Salesforce entries as array of objects
const salesforceEntries = salesforceProfiles.map(p => ({
  name: (p.Name || '').toLowerCase().trim(),
  company: (p.Account?.Name || '').toLowerCase().trim(),
}));

// Filter inputProfiles: keep only those NOT matching Salesforce on company and partial name
const uniqueProfiles = inputProfiles.filter(profile => {
  const firstName = (profile.firstName || '').toLowerCase().trim();
  const lastName = (profile.lastName || '').toLowerCase().trim();
  const company = (profile.companyName || '').toLowerCase().trim();

  // If company is missing, consider no match
  if (!company) return true;

  // Check if any Salesforce entry matches company exactly AND includes first or last name
  const isDuplicate = salesforceEntries.some(sf => {
    if (sf.company !== company) return false;
    // Check if Salesforce Name includes either firstName or lastName (partial match)
    return (firstName && sf.name.includes(firstName)) || (lastName && sf.name.includes(lastName));
  });

  // Keep only profiles NOT duplicates
  return !isDuplicate;
});

return uniqueProfiles;
